import { Component } from '@angular/core';
import { AnnouncementService } from 'src/app/services/announcement.service';

@Component({
  selector: 'app-announcement-details',
  templateUrl: './announcement-details.component.html',
  styleUrls: ['./announcement-details.component.scss'],
})
export class AnnouncementDetailsComponent {
  selectedAnnouncement = this.announcementService.selectedAnnouncement;

  constructor(private announcementService: AnnouncementService) {}

  ngOnInit(): void {
    this.selectedAnnouncement = this.announcementService.selectedAnnouncement;
  }
}
<div class="announcement-details" *ngIf="selectedAnnouncement">
  <h2>{{ selectedAnnouncement.title }}</h2>
  <img *ngIf="selectedAnnouncement.imageUrl" [src]="selectedAnnouncement.imageUrl" alt="{{ selectedAnnouncement.title }} Image" />
  <p>{{ selectedAnnouncement.content }}</p>
  <p class="date">Date: {{ selectedAnnouncement.date | date }}</p>
</div>
