import { Component, OnInit } from '@angular/core';
import { Publication } from '../../model/publication';
import { ActivatedRoute, Router } from '@angular/router';
import { DataService } from '../../services/data.service';

@Component({
  selector: 'app-publication',
  templateUrl: './publication.component.html',
  styleUrls: ['./publication.component.scss'],
})
export class PublicationComponent implements OnInit {
  publication: Publication | undefined;
  publicationId: number | undefined;

  publications: Publication[] | undefined;

  constructor(
    private route: ActivatedRoute,
    private dataService: DataService,
    private router: Router
  ) {}

  ngOnInit(): void {
    // this.publicationId = +this.route.snapshot.paramMap.get('id')!;
    // this.publication = this.dataService.getPublicationById(this.publicationId);
    // if (this.publication === undefined) {
    //   console.log('publicaton not found');
    // } else {
    //   console.log(JSON.stringify(this.publication));
    // }
    this.getPublications();
  }

  getPublications() {
    this.publications = this.dataService.getAllPublications();
    console.log(JSON.stringify(this.publications));
  }
}
<div class="container">
  <div class="publications" *ngIf="publications && publications.length > 0">
    <h3 class="publication-title">Publications:</h3>
    <ul>
      <li class="publication-list" *ngFor="let publication of publications">
        <div class="publication-list-card">
          <a *ngIf="publication.url" [href]="publication.url" target="_blank">
            {{ publication.title }}
          </a>
          <p class="year">{{ publication.year }}</p>
          <a class="button-link scholar" [href]="publication.googleScholar" target="_blank">
            <span>Google Scholar</span>
            <i class="material-icons">keyboard_arrow_right</i>
          </a>
          <!-- <a *ngIf="publication.pdf" [href]="publication.pdf" target="_blank"><div class="button">PDF</div></a> -->
        </div>
      </li>
    </ul>
  </div>
</div>
