import { Component, Input } from '@angular/core';
import { People } from '../../model/people';
import { Router } from '@angular/router';

@Component({
  selector: 'app-person-card',
  templateUrl: './person-card.component.html',
  styleUrls: ['./person-card.component.scss'],
})
export class PersonCardComponent {
  @Input() person!: People;

  constructor(private router: Router) {}

  //* ------------------------------------------------------------------------------------------------
  //* redirect to person's website
  redirectToPersonDetails() {
    console.log(this.person.url);
    if (this.person.url) {
      window.open(this.person.url, '_blank');
    }
  }
}
<div class="project-card glass-effect">
  <div class="image" *ngIf="person.image">
    <img [src]="person.image" alt="Person Image" />
  </div>
  <div class="content">
    <h2>{{ person.name }}</h2>
  </div>
  <div class="person-details">
    <p class="person-detail">{{ person.details }}</p>
    <button *ngIf="person.url" (click)="redirectToPersonDetails()" class="button-link link">
      <p>View more</p>
      <i class="material-icons">keyboard_arrow_right</i>
    </button>
  </div>
</div>
