import { Component, Input } from '@angular/core';
import { Project } from '../../model/project';
import { ActivatedRoute } from '@angular/router';
import { DataService } from '../../services/data.service';
import { Publication } from '../../model/publication';

@Component({
  selector: 'app-project-details',
  templateUrl: './project-details.component.html',
  styleUrls: ['./project-details.component.scss'],
})
export class ProjectDetailsComponent {
  project: Project | undefined;
  publications: Publication[] | undefined;

  constructor(
    private route: ActivatedRoute,
    private dataService: DataService
  ) {}

  ngOnInit() {
    // * Retrieve the project ID from the URL route parameter
    const projectId = +this.route.snapshot.paramMap.get('id')!;

    if (projectId) {
      console.log('Project ID:', projectId);
      this.project = this.dataService.getProjectById(projectId);
      if (this.project?.name === undefined) {
        console.log("Project doesn't exist");
      } else {
        console.log(this.project.name);
      }
    } else {
      console.log('Project ID not found in the URL.');
    }

    // * Get publications for this project
    this.publications = this.dataService.getPublicationsForProject(projectId);
  }
}
<div class="container">
  <div class="project-details" *ngIf="project">
    <h2 class="title">{{ project.name }}</h2>
    <p *ngIf="project.date" class="date">Date: {{ project.date }}</p>
    <img [src]="project.image" alt="Project Image" />

    <p class="description">{{ project.description }}</p>

    <div class="publications" *ngIf="publications && publications.length > 0">
      <h3 class="publication-title">Publications:</h3>
      <ul>
        <li class="publication-list" *ngFor="let publication of publications">
          <div class="publication-list-card">
            <a *ngIf="publication.url" [href]="publication.url" target="_blank"> {{ publication.title }} </a>
            <!-- <a *ngIf="publication.pdf" [href]="publication.pdf" target="_blank"><div class="button">PDF</div></a> -->
          </div>
        </li>
      </ul>
    </div>
    <div *ngIf="!publications || publications.length === 0">
      <p>No-publications</p>
    </div>
  </div>
</div>
